package com.wanghy.test.demo;

import java.util.LinkedList;
import java.util.List;

public class OOM {
	public static void main(String[] args) {
		List list = new LinkedList();
		String str = "";
		long i = 0;
		while (true) {
			String node = str;
			str += node;
			list.add(node);
			i++;
			if (i % 10 == 0) {
				System.out.println(i);
			}
		}
	}
}
